mapper.select:
SELECT stayregister.*,Predetermine.*,ReceiveTarget.*,room.* ,rentOutType.attributeDetailsName rentOutType ,payWay.attributeDetailsName payWay ,billunit.attributeDetailsName billunit ,passengerType.attributeDetailsName passengerType ,isBillON.attributeDetailsName isBill ,passenger.* FROM stayregister LEFT JOIN Predetermine ON stayregister.Predetermineid=Predetermine.Predetermineid INNER JOIN ReceiveTarget ON stayregister.receiveTargetId=ReceiveTarget.ReceiveTargetid INNER JOIN room ON stayregister.roomId=room.roomid INNER JOIN attributedetails rentOutType ON stayregister.rentOutTypeID=rentOutType.AttributeDetailsid LEFT JOIN attributedetails payWay ON stayregister.payWayId=payWay.AttributeDetailsid INNER JOIN attributedetails billunit ON stayregister.billunitid=billunit.AttributeDetailsid INNER JOIN attributedetails passengerType ON stayregister.passengerTypeId=passengerType.AttributeDetailsid INNER JOIN attributedetails isBillON ON stayregister.isBillID=isBillON.AttributeDetailsid LEFT JOIN StayRegisterDetails ON StayRegisterDetails.stayRegisterID=stayregister.stayRegisterID LEFT JOIN passenger ON StayRegisterDetails.passengerID=passenger.pId
and stayregister.isBillID=#{isBillID}
and stayregister.LvKeLeiXingId=#{LvKeLeiXingId}
order by stayregister.remind desc
